INSERT INTO `section_details` (`id`, `title`, `content`, `lang`, `section_id`) VALUES
(6, 'استلام قافلة مساعدات إغاثية من الأردن', '<p>تسلّمت منظمة #الهلال_الأحمر_العربي_السوري مساعدات إنسانية من مركز الملك سلمان للإغاثة والأعمال الإنسانية، وصلت براً عبر معبر نصيب الحدودي مع الأردن، وستتوجه لتغطية جزء من احتياجات العائلات الأشد ضعفاً في #درعا والضمير ودوما بـ #ريف_دمشق و #اللاذقية.\r\nوحملت المساعدات 60 شاحنة 8 منها مواد وتجهيزات طبية ستسلم لوزارة الصحة، ومواد غذائية وإغاثية تضم 3380 سلة غذائية، و13520 كيس طحين (10 كغ) و10208 حقائب إيواء، و13200 حقيبة صحية، و6555 قطعة ملابس شتوية.<br /></p>', 'ar', 16),
(7, 'Relief aid convoy from Jordan', '<p>The #SyrianArabRedCrescent received humanitarian aid from the King Salman Humanitarian Aid and Relief Center KSRelief, which arrived by land through the Nassib border crossing with Jordan.\r\nThis aid will be directed to cover part of the needs of the most vulnerable families in #Daraa, Al-Dumayr, and Douma in the #Damascus countryside and #Latakia. The convoy consisted of 60 trucks, including 8 carrying medical supplies and equipment that will be delivered to the Ministry of Health. The aid also included essential food and relief supplies, such as 3,380 food baskets, 13,520 bags of flour (10 kg each), 10,208 shelter bags, 13,200 health bags, and 6,555 pieces of winter clothing.<br /></p>', 'en', 16),
(8, 'بيان منظمة الهلال الأحمر العربي السوري', '<p>&nbsp;بيان صادرعن منظمة الهلال الأحمر العربي السوري<br /></p>', 'ar', 6),
(9, 'Statement by The Syrian Arab Red Crescent', '<p>Statement by The Syrian Arab Red Crescent<br /></p>', 'en', 6),
(10, 'توزيع المساعدات الإنسانية في ريف حمص', '<p>من عمليات توزيع المساعدات الإنسانية في ريف <span style=\"font-weight: bold;\">حمص</span>، لتخفيف معاناة العائلات الأشد ضعفاً خاصة الأسر التي بين أفرادها ذوي إعاقة ومرضى سرطان أو تعيلها نساء، وبدعم من <span style=\"font-style: italic;\">مركز الملك سلمان للإغاثة والأعمال الإنسانية</span>.<br />&nbsp;وتلقت حتى أمس السبت ٦١٠ عائلات في تلبيسة سلة غذائية مع طحين وسلة نظافة وحقيبة شتوية، و ٢٥٤ عائلة في الرستن سلة نظافة وحقيبة شتوية وكيس طحين.<br />&nbsp;<span style=\"font-style: italic;\">الهلال الأحمر العربي السوري</span><br /></p>', 'ar', 17),
(11, 'SARC distributing essential aid packages in Rural Homs', '<p>In rural <span style=\"font-weight: bold;\">Homs</span>, with the support of the <span style=\"font-style: italic;\">King Salman Center for Relief and Humanitarian Aid KSRelief</span>, the <span style=\"font-style: italic;\">Syrian Arab Red Crescent</span> is distributing essential aid packages to alleviate the suffering of the most vulnerable families.<br />This includes families with disabilities, cancer patients, and women who are the primary breadwinners.<br />To date, 610 families in Talbiseh have received food kits containing flour, hygiene kits, and winter clothing. In Rastan, 254 families have received hygiene kits, winter clothing, and bags of flour.<br /></p>', 'en', 17),
(12, 'المساعدات الإنسانية المقدمة من مركز الملك سلمان للإغاثة والأعمال الإنسانية', '<p>تسلّمت منظمة <span style=\"font-style: italic;\">الهلال الأحمر العربي السوري</span> الدفعتين الخامسة والسادسة من المساعدات الإنسانية المقدمة من<span style=\"font-style: italic;\"> مركز الملك سلمان للإغاثة والأعمال الإنسانية،</span> والتي وصلت عبر مطار<span style=\"font-weight: bold;\"> دمشق</span> الدولي وتضم 53.8 طن مواد غذائية وطحين وسلل نظافة وأدوية وحقائب شتوية وإيواء.\r\nالمساعدات ترسل مباشرة إلى ريف <span style=\"font-weight: bold;\">حمص</span>، حيث بدأت عمليات التوزيع للفئات الأشد ضعفاً.<br /></p>', 'ar', 18),
(13, 'Humanitarian aid from the King Salman Center for Relief and Humanitarian Aid KSRelief', '<p>\r\n\r\n<span style=\"color: rgb(65, 65, 65); font-size: 14px\">Today, the <span style=\"font-style: italic;\">Syrian Arab Red Crescent</span> received the fifth and sixth batch of humanitarian aid from the <span style=\"font-style: italic;\">King Salman Center for Relief and Humanitarian Aid KSRelief</span>. <br />The 27.9-ton shipment, which includes food supplies, flour, hygiene kits, and medicines, arrived at<span style=\"font-weight: bold;\"> Damascus </span>International Airport. <br />This aid is being distributed to the most vulnerable groups through the Talbiseh Distribution Center in <span style=\"font-weight: bold;\">Homs</span> countryside.</span>\r\n<br /></p>', 'en', 18),
(14, 'استقبال رئيسة اللجنة الدولية للصليب الأحمر', '<p>استقبل رئيس منظمة<span style=\"font-style: italic;\"> الهلال الأحمر العربي السوري </span>الدكتور حازم بقلة اليوم السبت، رئيسة<span style=\"font-style: italic;\"> اللجنة الدولية للصليب الأحمر</span> السيدة ميريانا سبولياريتش والوفد المرافق لها، وأكد الجانبان الشراكة الاستراتيجية المتينة بينهما لتخفيف المعاناة الإنسانية ومساعدة وحماية الأشد ضعفاً.<br />&nbsp;السيدة ميريانا سبولياريتش هنأت الدكتور حازم بقلة بتعيينه رئيساً للهلال الأحمر العربي السوري، وأعربت عن تقديرها العميق لمتطوعي وموظفي الهلال الأحمر العربي السوري وجهودهم المتواصلة لمساعدة الأشخاص والمجتمعات المتضررة مؤكدة التزام اللجنة بالشراكة الإنسانية لضمان تقديم المساعدات والخدمات <span style=\"font-weight: bold;\">الإنسانية</span> دون انقطاع في جميع أنحاء سوريا.\r\nبدوره عبّر الدكتور حازم بقلة عن شكره للدعم الذي تقدمه اللجنة الدولية للصليب الأحمر مبينا أن الاحتياجات الإنسانية في <span style=\"font-weight: bold;\">سوريا</span> بتزايد مستمر وهي بحاجة لتضافر وتعاون الجميع.<br /></p>', 'ar', 19),
(15, 'SARC receives ICRC President and Accompanied Delegation', '<p>\r\n\r\n<span style=\"color: rgb(65, 65, 65); font-size: 14px\">Dr. Hazem Bakleh, President of the<span style=\"font-style: italic;\"> Syrian Arab Red Crescent</span>, received the<span style=\"font-style: italic;\"> International Committee of the Red Cross </span>President Mirjana Spoljaric and her delegation today.<br />Both sides reaffirmed their strong strategic partnership to alleviate human suffering and protect the most vulnerable.<br />Ms. Spoljaric congratulated Dr. Bakleh on his appointment and expressed deep appreciation for the dedication of SARC volunteers and staff in assisting affected communities. She emphasized the ICRC\'s commitment to continued humanitarian partnership to ensure uninterrupted aid delivery across <span style=\"font-weight: bold;\">Syria</span>. Dr. Bakleh expressed gratitude for ICRC\'s support, acknowledging the escalating humanitarian needs in <span style=\"font-weight: bold;\">Syria</span> and the crucial importance of continued collaboration with all partners.</span>\r\n<br /></p>', 'en', 19),
(16, 'التعاون بين منظمة الهلال الأحمر العربي السوري و الدفاع المدني السوري', '<p>بحث رئيس منظمة <span style=\"font-style: italic;\">الهلال الأحمر العربي السوري</span> الدكتور حازم بقلة مع رئيس الدفاع المدني السوري رائد الصالح سبل التعاون والتنسيق بين الجانبين، وأهمية التعاون بين العاملين في المجال الإنساني، لتحقيق استجابة أكثر فعالية للاحتياجات المتزايدة وسط الأوضاع الصعبة التي تمر بها <span style=\"font-weight: bold;\">سوريا</span>.<br /></p>', 'ar', 20),
(17, 'SARC Collaborating with Syrian Civil Defense', '<p>\r\n\r\n<span style=\"color: rgb(65, 65, 65); font-size: 14px\"><span>&nbsp;</span>During a meeting, Dr. Hazem Bakleh, President of the <span style=\"font-style: italic;\">Syrian Arab Red Crescent</span>, and Raed Al-Saleh, Head of the <span style=\"font-style: italic;\">Syrian Civil Defense</span>, explored avenues for enhanced collaboration. They emphasized the critical importance of cooperation among humanitarian organizations in effectively addressing the escalating needs of the Syrian people amidst the ongoing challenging circumstances.</span>\r\n<br /></p>', 'en', 20),
